import React, { PureComponent } from 'react';
import { connect } from 'react-redux';

import { sendMessage } from '../../modules/message/actions';
import './style.css';


class SingleConversation extends PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      messageInput: '',
      showTimestamp: true,
      beginningOfFiveMinInterval: 0,
    };
  }

  onChangeInput = (e) => {
    this.setState({
      messageInput: e.target.value,
    });
  }

  maybeSubmit = (e) => {
    if (e.keyCode === 13) {
      e.preventDefault();
      this.props.dispatcher.sendMessage(this.state.messageInput, this.props.conversationId);
      this.setState({
        messageInput: '',
      });
    }
  }

    // if current message is created at a 5 minute mark, print time
  // if created within 0 - 5 mins, round down to the nearest 5 min mark
  getTime = (timeMS) => {
    // Get the current time in human readable format of the current ms
    const date  = new Date(timeMS);
    const time = date.getTime();
    const hour = date.getHours();
    const mins = date.getMinutes();
    const lastTime = this.state.beginningOfFiveMinInterval;
    const FIVEMINS = 5*60*1000;
    let showTimestamp = false;

    // find the difference between the current message time sent and the last message time sent and compare to five mins
    if((time - lastTime) >= FIVEMINS){
      this.setState({beginningOfFiveMinInterval: time})
      return <div>{time}</div>;
    }
  }

  render() {
    const {
      messages,
    } = this.props;

    const {
      showTimestamp,
      messageInput,
    } = this.state;

    return (
      <div className="drift-sidebar-single-conversation--container">
        <div className="drift-sidebar-single-conversation-body">
          {
            showTimestamp && <div>TEST</div>
          }
          {messages.map(message => <div key={message.id}>{message.body}</div>)}
        </div>
        <div className="drift-sidebar-single-conversation-input">
          <input placeholder="Type and press enter to send" value={messageInput} onChange={this.onChangeInput} onKeyDown={this.maybeSubmit} />
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => {
  const conversationId = state.conversation.selectedConversation;
  return {
    messages: state.message.byConversationId[conversationId] || [],
    conversationId,
  }
}

const mapDispatchToProps = dispatch => ({
  dispatcher: {
    sendMessage: (messageBody, conversationId) => dispatch(sendMessage({ body: messageBody, conversationId, })),
  }
})


export default connect(mapStateToProps, mapDispatchToProps)(SingleConversation);
