import React, { PureComponent } from 'react';
import { connect } from 'react-redux';

import { sendMessage } from '../../modules/message/actions';
import './style.css';


class SingleConversation extends PureComponent {
  constructor(props) {
    super(props);
    
    this.state = {
      messageInput: '',
      showTimestamp: true,
      beginningOfFiveMinInterval: 0,
    };
  }

  onChangeInput = (e) => {
    this.setState({
      messageInput: e.target.value,
    });
  }
  

  maybeSubmit = (e) => {
    if (e.keyCode === 13) {
      e.preventDefault();
      this.props.dispatcher.sendMessage(this.state.messageInput, this.props.conversationId);
      this.setState({
        messageInput: '',
      });
    }
  }

  // if current message is created at a 5 minute mark, print time
  // if created within 0 - 5 mins, round down to the nearest 5 min mark
  getTime = (timeMS) => {
    // Get the current time in human readable format of the current ms
    const date  = new Date(timeMS);
    const time = date.getTime();
    const hour = date.getHours();
    const mins = date.getMinutes();
    const lastTime = this.state.beginningOfFiveMinInterval;
    const FIVE_MINS = 5*60*1000;
    let showTimestamp = false;

    // find the difference between the current message time sent and the last message time sent and compare to five mins
    if((time - lastTime) >= FIVE_MINS){
      this.setState({beginningOfFiveMinInterval: time});
      return <div>{time}</div>;
    }
  }

  render() {

    
    const FIVE_MINS = 5*60*1000;
    // [message timestamp, [array of messages for the interval]]
    let timeStampMap = new Map();

    const {
      messages,
    } = this.props;

    const {
      messageInput,
    } = this.state;

    // Source: https://stackoverflow.com/questions/61742739/how-to-group-js-array-messages-in-1-minute-increments
    // calculating 5 min intervals:
    // 1618754891000(10:09am)-1618754711000(10:05am)= 180000/FIVE_MINS = 0.6 <- round down
    // map -> {(0, [messages])}
    // all messages sent within 10:04am to 10:09am will be mapped to array in key 0
    // 1618755311000 (10:15am)-1618754711000(10:05am)  = 600000/FIVE_MINS =  2
    // map -> {(0,[messages]), (2, [messages])}
    // every time a new message is sent, the single conversation component is rendered so if you send a message now,
    // the div above the message will display "Now", then 10 mins later, it will display "10 mins ago"
    messages.forEach(message => {
      let currentTime = new Date(Date.now());
      currentTime.setSeconds(0);
      currentTime.setMilliseconds(0);
      let messageTime = new Date(message.createdAt);
      messageTime.setSeconds(0);
      messageTime.setMilliseconds(0);
      // console.log("current time: " + currentTime.getTime());
      // console.log("message time: " + messageTime.getTime());
      let key = Math.floor((currentTime.getTime() - messageTime.getTime()) / FIVE_MINS);

      if(!timeStampMap.has(key)){
        timeStampMap.set(key, [message]);
      } else {
        timeStampMap.get(key).push(message);
      }
    });

    console.log(timeStampMap);

    let timeStampIntervals = [...timeStampMap.keys()];
    console.log(timeStampIntervals);

    return (
      <div className="drift-sidebar-single-conversation--container">
        <div className="drift-sidebar-single-conversation-body">
        {}
          {/* {messages.map(message => <div key={message.id}>{message.body}</div>)} */}

        </div>
        <div className="drift-sidebar-single-conversation-input">
          <input placeholder="Type and press enter to send" value={messageInput} onChange={this.onChangeInput} onKeyDown={this.maybeSubmit} />
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => {
  const conversationId = state.conversation.selectedConversation;
  return {
    messages: state.message.byConversationId[conversationId] || [],
    conversationId,
  }
}

const mapDispatchToProps = dispatch => ({
  dispatcher: {
    sendMessage: (messageBody, conversationId) => dispatch(sendMessage({ body: messageBody, conversationId, })),
  }
})


export default connect(mapStateToProps, mapDispatchToProps)(SingleConversation);
